package abc;

public class DataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int max = 2147483647;
		int min = -2147483648;
		short shortMax = 32767;
		short shortMin = -32768;
		long longMax = 9223372036854775807l;
		long longMin = -9223372036854775808l;
		byte byteMax = 127;
		byte byteMin = -128;
		System.out.println("Int max: "+max+" Int min "+min);
		System.out.println("Short max: "+shortMax+" Short min "+shortMin);
		System.out.println("Byte max: "+byteMax+" Byte min "+byteMin);
		System.out.println("long max: "+longMax+" long min "+longMin);
	}

}
